  console.log('='.repeat(50));
  console.log(`Server running at http://localhost:${PORT}`);
  console.log(`Dashboard available at http://localhost:${PORT}/`);
  console.log(`API endpoint: http://localhost:${PORT}/api/report`);
  console.log('='.repeat(50));
